def get_transaction_size(tx_data):
    # Remove the "0x" prefix if present
    cleaned_tx_data = tx_data[2:] if tx_data.startswith('0x') else tx_data
    
    # Calculate the size in bytes (each 2 hex chars represent 1 byte)
    tx_size_in_bytes = len(cleaned_tx_data) // 2
    return tx_size_in_bytes

# Example transaction data
tx_data = ""

# Calculate and print the transaction size
tx_size = get_transaction_size(tx_data)
print(f"Transaction size: {tx_size} bytes")
